import{_ as e,c as t,al as a,o as l}from"./chunks/framework.NFAqBSgQ.js";const b=JSON.parse('{"title":"fastboot fastbootd","description":"","frontmatter":{},"headers":[],"relativePath":"en/applications/system/fastboot/index.md","filePath":"en/applications/system/fastboot/index.md"}'),d={name:"en/applications/system/fastboot/index.md"};function s(i,o,r,c,n,m){return l(),t("div",null,o[0]||(o[0]=[a('<h1 id="fastboot-fastbootd" tabindex="-1"><code>fastboot</code> fastbootd <a class="header-anchor" href="#fastboot-fastbootd" aria-label="Permalink to &quot;`fastboot` fastbootd&quot;">​</a></h1><h2 id="prepare" tabindex="-1">Prepare <a class="header-anchor" href="#prepare" aria-label="Permalink to &quot;Prepare&quot;">​</a></h2><ul><li>Check fastboot tool(Host): <code>fastboot --version</code></li><li>Download fastboot tool and install(Host): <a href="https://developer.android.com/tools/releases/platform-tools" target="_blank" rel="noreferrer">platform-tools</a></li><li>Enable the fastbootd application(Device): <code>CONFIG_USBFASTBOOT=y</code> and <code>CONFIG_SYSTEM_FASTBOOTD=y</code></li><li>Start fastbootd(Device): <code>fastbootd &amp;</code></li></ul><h2 id="commands" tabindex="-1">Commands <a class="header-anchor" href="#commands" aria-label="Permalink to &quot;Commands&quot;">​</a></h2><ul><li><p><code>fastboot reboot [FLAG]</code>: Reboot the device, more details for <code>[FLAG]</code>: <a href="https://github.com/apache/nuttx-apps/blob/master/nshlib/nsh_syscmds.c#L114" target="_blank" rel="noreferrer">g_resetflag</a> and <a href="https://github.com/apache/nuttx/blob/master/include/sys/boardctl.h#L458" target="_blank" rel="noreferrer">boardioc_softreset_subreason_e</a></p></li><li><p><code>fastboot flash &lt;PARTITION&gt; &lt;FILENAME&gt;</code>: Flash partition <code>&lt;PARTITION&gt;</code> using the given <code>&lt;FILENAME&gt;</code></p></li><li><p><code>fastboot erase &lt;PARTITION&gt;</code>: Erase given partition</p></li><li><ul><li><p>Get Variables</p><ul><li><code>fastboot getvar product</code>: Get product name</li><li><code>fastboot getvar kernel</code>: Get kernel name</li><li><code>fastboot getvar version</code>: Get OS version string</li><li><code>fastboot getvar slot-count</code>: Get slot count</li><li><code>fastboot getvar max-download-size</code>: Get max download size</li></ul></li></ul></li><li><ul><li><p>OEM</p><ul><li><code>fastboot oem filedump &lt;PARTITION&gt; [OFFSET] [LENGTH]</code>: Get <code>&lt;LENGTH&gt;</code> (full by default) bytes of <code>&lt;PARTITION&gt;</code> from <code>&lt;OFFSET&gt;</code> (zero by default)</li><li><code>fastboot oem memdump &lt;ADDRESS&gt; &lt;LENGTH&gt;</code>: Dump <code>&lt;LENGTH&gt;</code> bytes memory from address <code>&lt;ADDRESS&gt;</code></li><li><code>fastboot oem shell &lt;COMMAND&gt;</code>: Execute custom commands. e.g. &quot;oem shell ps&quot;, &quot;oem shell ls /dev/&quot;</li></ul></li></ul></li><li><p><code>fastboot get_staged &lt;OUT_FILE&gt;</code>: Writes data staged by the last command to file <code>&lt;OUT_FILE&gt;</code>. e.g. &quot;oem filedump&quot; and &quot;oem memdump&quot;</p></li></ul><h2 id="examples" tabindex="-1">Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;Examples&quot;">​</a></h2><ul><li>Exit fastboot mode: <code>fastboot reboot</code></li><li>Flash app.bin to partition /dev/app: <code>fastboot flash app ./app.bin</code></li><li>Erase partition /dev/userdata: <code>fastboot erase userdata</code></li><li>Dump partition /dev/app: <code>fastboot filedump /dev/app</code> and then <code>fastboot get_staged ./dump_app.bin</code></li><li>Dump memory from 0x44000000 to 0x440b6c00: <code>fastboot oem memdump 0x44000000 0xb6c00</code> and then <code>fastboot get_staged ./mem_44000000_440b6c00.bin</code></li><li>Create RAM disk &quot;/dev/ram10&quot; of size 320KB: <code>fastboot oem shell &quot;mkrd -m 10 -s 512 640&quot;</code></li></ul>',7)]))}const f=e(d,[["render",s]]);export{b as __pageData,f as default};
