import{_ as t,c as a,al as o,o as n}from"./chunks/framework.NFAqBSgQ.js";const h=JSON.parse('{"title":"Configuring a Wireless Network","description":"","frontmatter":{},"headers":[],"relativePath":"en/applications/wireless/wapi/wireless.md","filePath":"en/applications/wireless/wapi/wireless.md"}'),i={name:"en/applications/wireless/wapi/wireless.md"};function s(l,e,r,d,c,p){return n(),a("div",null,e[0]||(e[0]=[o(`<h1 id="configuring-a-wireless-network" tabindex="-1">Configuring a Wireless Network <a class="header-anchor" href="#configuring-a-wireless-network" aria-label="Permalink to &quot;Configuring a Wireless Network&quot;">​</a></h1><p><code>wapi</code> is used to connect to an Access Point by setting its parameters.</p><p>Note</p><p>Usually, one single wireless interface refers to an operational mode: Station Mode (STA) or SoftAP. For instance, <code>wlan0</code> may refer to the STA mode and <code>wlan1</code>. Setting the passphrase and the ESSID of a STA-enabled interface would make it connect to a wireless network while setting the same parameters for a SoftAP-enabled interface would provide a network wireless with these connection parameters. Please check <code>ESP32 Wi-Fi Station Mode (wlan0) &lt;esp32_wi-fi_sta&gt;</code>{.interpreted-text role=&quot;ref&quot;} and <code>ESP32 Wi-Fi SoftAP Mode (wlan1) &lt;esp32_wi-fi_softap&gt;</code>{.interpreted-text role=&quot;ref&quot;} sections.</p><h2 id="setting-the-passphrase" tabindex="-1">Setting the Passphrase <a class="header-anchor" href="#setting-the-passphrase" aria-label="Permalink to &quot;Setting the Passphrase&quot;">​</a></h2><p><code>wapi psk</code> command is used for setting the AP authentication security. Its arguments are:</p><pre><code>wapi psk &lt;ifname&gt; &lt;passphrase&gt; &lt;index/flag&gt; [wpa]
</code></pre><ul><li><code>&lt;ifname&gt;</code> is the name of the interface set as Station Mode (STA);</li><li><code>&lt;passphrase&gt;</code> is the password. Its length depends on the authentication algorithm. Please note that <code>wapi psk</code> command is also used to set auth as open, but this parameter comes next, so it may be necessary to set a &quot;dummy&quot; passphrase just to set auth to none;</li><li><code>&lt;index/flag&gt;</code> can be set numerically or textually, as follows:</li></ul><blockquote><ul><li>[0] WPA_ALG_NONE - to connect to an open AP;</li><li>[1] WPA_ALG_WEP - to connect to a WEP-secured AP (not recommended);</li><li>[2] WPA_ALG_TKIP - to use TKIP algorithm (not recommended);</li><li>[3] WPA_ALG_CCMP - to use CCMP algorithm (recommended);</li></ul></blockquote><ul><li><code>[wpa]</code> sets the WPA version (if applicable):</li></ul><blockquote><ul><li>[0] WPA_VER_NONE;</li><li>[1] WPA_VER_1;</li><li>[2] WPA_VER_2 (default, if not selected otherwise);</li><li>[3] WPA_VER_3;</li></ul></blockquote><h2 id="setting-the-network-name-essid" tabindex="-1">Setting the Network Name (ESSID) <a class="header-anchor" href="#setting-the-network-name-essid" aria-label="Permalink to &quot;Setting the Network Name (ESSID)&quot;">​</a></h2><p>The name of the Wireless Network can be set using <code>wapi essid</code> command:</p><pre><code>wapi essid &lt;ifname&gt; &lt;essid&gt; &lt;index/flag&gt;
</code></pre><ul><li><code>&lt;ifname&gt;</code> is the name of the interface set as Station Mode (STA);</li><li><code>&lt;essid&gt;</code> is the name of the Wireless Network;</li><li><code>&lt;index/flag&gt;</code> selects whether to connect to the AP or not:</li></ul><blockquote><ul><li>[0] WAPI_ESSID_OFF - Don&#39;t connect to the AP;</li><li>[1] WAPI_ESSID_ON - Connect to the AP indicated by the provided ESSID;</li><li>[2] WAPI_ESSID_DELAY_ON - Delay AP connection;</li></ul></blockquote><h2 id="examples" tabindex="-1">Examples <a class="header-anchor" href="#examples" aria-label="Permalink to &quot;Examples&quot;">​</a></h2><h3 id="connecting-to-an-open-network" tabindex="-1">Connecting to an Open Network <a class="header-anchor" href="#connecting-to-an-open-network" aria-label="Permalink to &quot;Connecting to an Open Network&quot;">​</a></h3><pre><code>wapi psk wlan0 mypasswd WPA_ALG_NONE
wapi essid wlan0 myssid WAPI_ESSID_ON
</code></pre><p>or, equivalently</p><pre><code>wapi psk wlan0 mypasswd 0
wapi essid wlan0 myssid 1
</code></pre><h3 id="connecting-to-a-wpa2-psk-network" tabindex="-1">Connecting to a WPA2-PSK Network <a class="header-anchor" href="#connecting-to-a-wpa2-psk-network" aria-label="Permalink to &quot;Connecting to a WPA2-PSK Network&quot;">​</a></h3><pre><code>wapi psk wlan0 mypasswd WPA_ALG_CCMP 
wapi essid wlan0 myssid WAPI_ESSID_ON
</code></pre><p>or, equivalently:</p><blockquote><pre><code>wapi psk wlan0 mypasswd 3
wapi essid wlan0 myssid 1
</code></pre></blockquote><h3 id="connecting-to-a-wpa3-sae-network" tabindex="-1">Connecting to a WPA3-SAE Network <a class="header-anchor" href="#connecting-to-a-wpa3-sae-network" aria-label="Permalink to &quot;Connecting to a WPA3-SAE Network&quot;">​</a></h3><pre><code>wapi psk wlan0 mypasswd WPA_ALG_CCMP WPA_VER_3
wapi essid wlan0 myssid WAPI_ESSID_ON
</code></pre><p>or, equivalently:</p><pre><code>wapi psk wlan0 mypasswd 3 3
wapi essid wlan0 myssid 1
</code></pre><h3 id="connecting-to-a-hidden-wpa2-psk-network" tabindex="-1">Connecting to a Hidden (WPA2-PSK) Network <a class="header-anchor" href="#connecting-to-a-hidden-wpa2-psk-network" aria-label="Permalink to &quot;Connecting to a Hidden (WPA2-PSK) Network&quot;">​</a></h3><pre><code>wapi psk wlan0 mypasswd WPA_ALG_CCMP 
wapi essid wlan0 myssid WAPI_ESSID_DELAY_ON
wapi ap wlan0 aa:bb:cc:dd:dd:ff
</code></pre><p>or, equivalently:</p><pre><code>wapi psk wlan0 mypasswd 3
wapi essid wlan0 myssid 2
wapi ap wlan0 aa:bb:cc:dd:dd:ff
</code></pre>`,33)]))}const m=t(i,[["render",s]]);export{h as __pageData,m as default};
